tinymce.create('tinymce.plugins.verticalspace', {
	init : function(ed, url) {
		values = [];
		for (var h = 10; h < 200; h += 10) { 
			values.push({text: String(h) + 'px', value:  h}); // ...and add them to the menu
		};
			
		ed.addButton( 'verticalspace', {
			type: 'listbox',
			text: '',
			image: tinymce.baseURL + '/plugins/verticalspace/vertical-space.png',
			icon: tinymce.baseURL + '/plugins/verticalspace/vertical-space.png',
			onselect: function(v) {
				var divSpace = "<div class=\"editeurSpace\" style=\"clear:both;min-height:"+this.value()+"px;\"></div>" ;
				ed.selection.setContent(divSpace);
				return false;
			},
			values: values,
			onPostRender: function () {
				// Select the second item by default
				this.value('100');
			 }
		});
	}
});

// register our custom plugin
tinymce.PluginManager.add('verticalspace', tinymce.plugins.verticalspace);
